import React, { Component } from "react";
import {Button, ButtonToolbar, Card, Container, Row} from "react-bootstrap";
import AddHelp_guaidModal from "./AddHelp_guaidModal";
import axios from "axios";

export default class HelpGuide extends Component {
    constructor(props) {
        super(props);
        this.state ={
            addModalShow: false,
            posts:[]
        }
    }
    async componentDidMount() {
        try{
            const res = await axios.get('http://localhost:8000/api/all-posts');
            console.log(res.data)
            this.setState({ posts: res.data });
        }catch (err){
            console.log("error occurred")
        }
    }

    render() {
        const { posts } = this.state;
        return (

            <Container>
                <Row className="mt-5">
                    <AddHelp_guaidModal />
                {/*<ButtonToolbar>*/}
                {/*    <Button variant='primary'*/}
                {/*            onClick={() => this.setState({ addModalShow: true })}*/}
                {/*    >Add Category*/}
                {/*    </Button>*/}
                {/*    <AddHelp_guaidModal*/}
                {/*        show={this.state.addModalShow}*/}
                {/*        onHide={AddModelClose}*/}
                {/*    />*/}
                {/*</ButtonToolbar>*/}
                </Row>
                {posts.map(post =>
                <Row className="mt-5">
                    <Card className="bg-white w-75 h-100">
                        <p>{post.id}</p>
                            <p>{post.link}</p>
                                <p>{post.description}</p>
                    </Card>
                </Row>
                )}

            </Container>

        );
    }
}
