import React, { Component } from "react";
import axios from "axios";
import {Modal} from "react-bootstrap";

export default class HelpGuide extends Component {
    constructor(props) {
        super(props);
        this.state ={
            link:'',
            description:'',
        }
    }

    // async handleFormSubmit(event){
    //     event.preventDefault();
    //     let email = this.state.username,
    //         password = this.state.password;
    //     try{
    //
    //         const res = await axios.post('http://localhost:8000/api/login',{
    //             email,
    //             password
    //         });
    //         console.log(res)
    //         if(res.data === "Done"){
    //             this.props.history.push('/stat-report');
    //         }
    //         if (res.data === "Fails"){
    //             alert("please enter correct user name and password")
    //         }
    //     }catch (err){
    //         console.log("error occurred")
    //     }
    // }
    // handleChecked(){
    //     this.setState({isChecked: !this.state.isChecked})
    // }
    render() {
        const {link,description} = this.state;
        return (

                        <Modal.Dialog>
                            <Modal.Header closeButton>
                                <Modal.Title>Add Help</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <form onSubmit={event => this.handleFormSubmit(event)}>
                                    <div className="form-group">
                                        <label>Link</label>
                                        <input
                                            name="link"
                                            type="text"
                                            className="form-control"
                                            placeholder="Enter link"
                                            value={link}
                                            onChange={ event => this.setState({link: event.target.value})}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Description</label>
                                        <input
                                            name="description"
                                            type="text"
                                            className="form-control"
                                            placeholder="Enter password"
                                            value={description}
                                            onChange={ event => this.setState({description: event.target.value})}
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary btn-block mb-5">Add</button>
                                </form>
                            </Modal.Body>

                            <Modal.Footer>
                                <Button variant="secondary">Close</Button>
                                <Button variant="primary">Save changes</Button>
                            </Modal.Footer>
                        </Modal.Dialog>

        );
    }
}
