import React, { Component } from "react";
import { Card,Container,Row,Col } from 'react-bootstrap';

export default class stateReport extends Component {

        state = {
            covid_data:[],
        };

    componentDidMount() {
    fetch("https://hpb.health.gov.lk/api/get-current-statistical")
        .then(res => res)
        .then(
            (result) => {
                // console.log(result.data.global_deaths)
                this.state.local_active_cases =  result.data.global_deaths ;
                console.log('******',this.state.local_active_cases)
            },
        )

    }
    render() {
        const {local_active_cases} = this.state;
        return (

            <Container  className="card-wrapper" >

                <Row>
                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>local_active_cases</Card.Title>
                                <Card.Text>
                                   <b>{local_active_cases}</b>

                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>Card Title</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Card.Link href="#">Card Link</Card.Link>
                                <Card.Link href="#">Another Link</Card.Link>
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>Card Title</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Card.Link href="#">Card Link</Card.Link>
                                <Card.Link href="#">Another Link</Card.Link>
                            </Card.Body>
                        </Card>
                    </Col>


                </Row>
            </Container>


        );
    }
}
