import React, { Component } from "react";
import { Card,Container,Row,Col } from 'react-bootstrap';

export default class stateReport extends Component {

        state = {
            total_d:'',
            global_deaths:'',
            global_new_cases:'',
        };

    componentDidMount() {
    fetch("https://hpb.health.gov.lk/api/get-current-statistical")
        .then(Response => Response.json())
        .then(data => {
            console.log(data.data)
            // console.log(this.state.local_recovered)
            this.setState({ total_d: data.data.global_deaths });


        })

    }
    render() {
        // const {covid_data} = this.state;
        return (

            <Container  className="card-wrapper" >

                <Row>

                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>local_active_cases</Card.Title>
                                <Card.Text>
                                    <h1>{this.state.total_d}</h1>
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>

                </Row>
            </Container>


        );
    }
}
