        };

    componentDidMount() {
    fetch("https://hpb.health.gov.lk/api/get-current-statistical")
        .then(Response => Response.json())
        .then(data => {
            this.setState({ covid_data: data });
        })

    }
    render() {
        const {local_active_cases} = this.state;
        return (

            <Container  className="card-wrapper" >

                <Row>
                    {cate.map(category =>
                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>local_active_cases</Card.Title>
                                <Card.Text>
                                   <b>{local_active_cases}</b>

                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                    )}
                </Row>
            </Container>


        );
    }
}
