import React, { Component } from "react";

export default class Login extends Component {

    state ={
            username:'',
            password:'',
            isChecked:false
    }
    handleFormSubmit(event){


        console.log(this.state.username,this.state.password);
        event.preventDefault();
        //alert(event.target.name.value);
        fetch('http://127.0.0.1:8000/register', {
            method: 'POST',
            headers: {

            },
            body: JSON.stringify({

            })
        })
            .then(res => res.json())
            .then((result) => {
                    //alert("Success");
                    if (result.name === undefined && result.status === undefined) {
                        this.setState({ snackbaropen: true, snackbarmsg: result.messages })
                    }
                    else if (result.name === undefined && result.status !== undefined) {
                        this.setState({ snackbaropen: true, snackbarmsg: result.status })
                    }
                    else if (result.name !== undefined && result.status === undefined) {
                        this.setState({ snackbaropen: true, snackbarmsg: result.name })
                    }

                    // console.log("result" , result.messages)
                }, (error) => {
                    // this.setState({ snackbaropen: true, snackbarmsg: 'Failed' })
                }

            )

    }
    handleChecked(){
        this.setState({isChecked: !this.state.isChecked})
    }
    render() {
        const {username,password,isChecked} = this.state;
        return (
            <div className="auth-wrapper">
            <div className="auth-inner">
            <form onSubmit={event => this.handleFormSubmit(event)}>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input
                        name="name"
                        type="email"
                        className="form-control"
                        placeholder="Enter email"
                        value={username}
                        onChange={ event => this.setState({username: event.target.value})}
                    />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input
                        name="password"
                        type="password"
                        className="form-control"
                        placeholder="Enter password"
                        value={password}
                        onChange={ event => this.setState({password: event.target.value})}
                    />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input
                            type="checkbox"
                            className="custom-control-input"
                            id="customCheck1"
                            checked={isChecked}
                            onChange={() => this.handleChecked()}
                        />
                        <label className="custom-control-label" htmlFor="customCheck1" onClick={() => this.handleChecked()}>Remember me</label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary btn-block">Submit</button>
                <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p>
            </form>
            </div>
            </div>
        );
    }
}
